<?php

namespace TracyClean\Bridges\Tracy;

use Exception;
use Latte\Engine;
use Nette\Application\Application;
use Nette\Bridges\ApplicationLatte\UIMacros;
use Nette\DI\Container;
use Nette\SmartObject;
use Tracy\IBarPanel;


/**
 * Class Panel
 *
 * @author  geniv
 * @package TracyClean\Bridges\Tracy
 */
class Panel implements IBarPanel
{
    use SmartObject;

    /** @var Container container from DI */
    private $container;
    /** @var array */
    private $parameters;


    /**
     * Panel constructor.
     *
     * @param array     $parameters
     * @param Container $container
     */
    public function __construct(array $parameters, Container $container)
    {
        $this->container = $container;

        $this->parameters = $parameters;
    }


    /**
     * Renders HTML code for custom tab.
     *
     * @return string
     */
    public function getTab()
    {
        return '<span title="Tracy clean"><img width="16px" height="16px" src="data:image/svg+xml;base64," />' .
            'Tracy clean' .
            '</span>';
    }


    /**
     * Renders HTML code for custom panel.
     *
     * @return string
     */
    public function getPanel()
    {
        $application = $this->container->getByType(Application::class);    // load system application
        $presenter = $application->getPresenter();

        // catch trait problem
        try {
            $this->parameters['InternalTracyClean'] = ($presenter ? $presenter->link('InternalTracyClean!') : null);
            $this->parameters['InternalDisableDebug'] = ($presenter ? $presenter->link('InternalDisableDebug!') : null);
        } catch (Exception $e) {
        }

        $this->parameters['presenter'] = $presenter;

        // catch latte problem
        try {
            $latte = new Engine;
            $latte->addProvider('uiControl', $presenter);
            UIMacros::install($latte->getCompiler()); // n:href, {link }
            return $latte->renderToString(__DIR__ . '/PanelTemplate.latte', $this->parameters);
        } catch (Exception $e) {
            return $e->getMessage();
        }
    }
}
